function initTweetUI(){
  var tweets = $("#rightcolumn .innertube .tweetarchives");
  $("div.body",tweets).hover(function(){$(this).addClass("hover");},function(){$(this).removeClass("hover");});
  $("span.prevbt,span.respbt",tweets).click(function(e){
    var id = this.getAttribute("id").replace(/^(prev|resp)_/,"");
    var preview = toggleTweetByID(id);
    preview.toggle();
    postToggle(id,preview);
    e.preventDefault();
    return false;
  });

  $("div.body td.action span").parent().hover(function(){
    var parent = $(this).parentsUntil("div.body,div.msg").last().parent();
    $("td.label",parent).html($(this).attr("title").split(" ")[0]+" &nbsp; |");
  },function(){
    var parent = $(this).parentsUntil("div.body,div.msg").last().parent();
    $("td.label",parent).html("");
  });

  //$("#tweets div.body").click(handleTap);
  $("td.text a span.replybt",tweets).parent().click(replyTweet);
  $("td.text a span.retweetbt",tweets).parent().click(retweet);
}

$(function(){
  initTweetUI();
});
