function initTweetUI(){
  var tweets = $("#tweets");
  $("div.body",tweets).hover(function(){$(this).addClass("hover");},function(){$(this).removeClass("hover");});
  $("span.prevbt,span.respbt",tweets).click(function(e){
    var id = this.getAttribute("id").replace(/^(prev|resp)_/,"");
    var preview = toggleTweetByID(id);
    preview.toggle();
    postToggle(id,preview);
    e.preventDefault();
    return false;
  });

  $("div.body td.action span").parent().hover(function(){
    var parent = $(this).parentsUntil("div.body,div.msg").last().parent();
    $("td.label",parent).html($(this).attr("title").split(" ")[0]+" &nbsp; |");
  },function(){
    var parent = $(this).parentsUntil("div.body,div.msg").last().parent();
    $("td.label",parent).html("");
  });

  //$("#tweets div.body").click(handleTap);
  $("td.text a span.replybt",tweets).parent().click(replyTweet);
  $("td.text a span.retweetbt",tweets).parent().click(retweet);
  $("a.moreofuser").click(showmore);
}

function initPortalUI(){
  if($("div#leftpane div.portal").length > 0 && $("div#rightpane div.portal").length > 0) return;
}

$(function(){
  if(!window.autorefresh) {
    if(!window.isHome) initTweetUI();
    initPortalUI();
  }
  /*Toggle Sidebar menu*/
  $('span.expandcollapse,span.collapseexpand').click(function() {
	var id = '#'+this.getAttribute("id").replace(/^(ex|col)_/,"") + "_navigation";
	$(id).slideToggle('slow');	
	if ($(this).hasClass('expandcollapse'))
	{
		$(this).removeClass('expandcollapse');
		$(this).addClass('collapseexpand');
	}
	else
	{
		$(this).removeClass('collapseexpand');
		$(this).addClass('expandcollapse');		
	}
  });

});
